import { useContext } from 'react';
import ShopContext from '../context/ShopContext';
import Item from '../components/Item/Item'
const ShopCategory = (props) => {
  const  {allAnimalsData}  = useContext(ShopContext);

  
  return (
    <div>
      <h1>{props.category.toUpperCase()}s for Sale</h1>
      <div className="main-holder">
      <img src={props.banner} alt="" />
        </div>
      {console.log(allAnimalsData[0].breed)}
        <div className="banner-holder">
      {allAnimalsData.map((item,i)=>{
        if (props.category === item.category) {   
          return (
            <Item key={i} image={item.image} breed={item.breed} price={item.price} gender={item.gender}/>
          )
        }
        else{
          return  null
        }
      })}
      </div>
    </div>
  );
  }

  // Filter animals based on category prop
  // const filteredAnimals = allAnimalData.filter(item => item.category === props.category);



export default ShopCategory;
