import React, { useContext } from 'react';
import ShopContext from '../context/ShopContext';
import Item from '../components/Item/Item';

const ShopCategory = (props) => {
  const { allAnimalData } = useContext(ShopContext);

  return (
    <div>
      This is Shop Category Page
      <img src={props.banner} alt="Banner" />
      <h1>This is animal Data</h1>
      {allAnimalData.map((item, i) => {
        if (item.category === props.category) {
          return <Item image={item.image} key={i} />;
        } else {
          return null; // or another fallback component/message
        }
      })}
    </div>
  );
};

export default ShopCategory;
