// ShopCategory.js
import React, { useContext } from 'react';
import ShopContext from '../context/ShopContext';
import Item from '../components/Item/Item';

const ShopCategory = (props) => {
  const { allAnimalData } = useContext(ShopContext);

  // Filter animals based on category prop
  const filteredAnimals = allAnimalData.filter(item => item.category === props.category);

  return (
    <div>
      <h1>{props.category.toUpperCase()}s for Sale</h1>
      {filteredAnimals.map((item) => (
        <div key={item.id}>
          <h2>{item.breed}</h2>
          <img src={item.image} alt={item.breed} />
          <p>Price: {item.price}</p>
          <p>Gender: {item.gender}</p>
          <p>Weight: {item.weight}</p>
          <p>Height: {item.height}</p>
        </div>
      ))}
    </div>
  );
};

export default ShopCategory;
