  }
  useEffect(() => {
    console.log(cartItem);
  }, [cartItem]);

  const contextValue = {
    getTotalCartAmount,
    allAnimalsData,
    cartItem,
    addToCart,
    removeFromCart,
  };
  return (
    <ShopContext.Provider value={contextValue}>
      {props.children}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
