import React, { useContext } from 'react'
import './CartItem.css'
import ShopContext from '../../context/ShopContext'
const CartItem = () =>{
    const {allAnimalsData,cartItem,addToCart,removeFromCart} = useContext(ShopContext);
  return (
    <div>
      {allAnimalsData.map((e)=>{
        if(cartItem[e.id] > 0){
            return(
                <div>
                    <img src={e.image} alt="" />
                </div>
            )
        }
     
      })}
    </div>
  )
}

export default CartItem
