import { useContext } from 'react';
import ShopContext from '../context/ShopContext';
import Item from '../components/Item/Item';

const ShopCategory = (props) => {
  const { allAnimalData } = useContext(ShopContext);

  // Check if allAnimalData exists and is an array
  // if (!Array.isArray(allAnimalData)) {
  //   return <div>Loading...</div>; // or handle loading state as per your UI
  
  return (
    <div>
      <h1>{props.category.toUpperCase()}s for Sale</h1>
      <img src={props.banner} alt="" />
        <div key={allAnimalData}>
          <h2>{allAnimalData}</h2>
          <img src={allAnimalData} alt={allAnimalData} />
          <p>Price: {allAnimalData}</p>
          <p>Gender: {allAnimalData}</p>
          <p>Weight: {allAnimalData}</p>
          <p>Height: {allAnimalData}</p>
        </div>
      
    </div>
  );
  }

  // Filter animals based on category prop
  // const filteredAnimals = allAnimalData.filter(item => item.category === props.category);



export default ShopCategory;
